import React from 'react';
import '../styles/About.css';

const About = () => {
    return (
        <div className="about">
            <h1>About Us</h1>
            <p>At Save Mother Nature, we are dedicated to raising awareness about climate change, advocating for sustainability, and taking actionable steps to preserve our planet.</p>
            <h2>Our Mission</h2>
            <p>We aim to provide tools, resources, and platforms for individuals, communities, and organizations to engage in climate action.</p>
            <h2>Our Vision</h2>
            <p>A world where people work together to protect natural resources and reduce the impact of human activities on the environment.</p>
        </div>
    );
};

export default About;
